import React from 'react'
import {Link} from 'react-router-dom'
import { useState } from 'react'
import axios from "axios"

const Login = () => {

    //useState for the inputs for register page
    const [inputs,setInputs] = useState({
        username:"",
        password:"",
    })

    //function to handle change in input for register page
    const handleChange = e => {
        setInputs(prev=>({...prev, [e.target.name]: e.target.value}))
    }

    //debugging purposes
    console.log(inputs)

    const handleSubmit = async e => {
        e.preventDefault()
        console.log("click")
        //api call
        try{
            //when call goes through
            const res = await axios.post("http://localhost:8800/auth/login",inputs)
            console.log(res)
        }catch(err)
        {
            //when theres an error
            console.log(err)
        }
        }



    return(
        <div className='auth'>
            <h1>Login</h1>
            <form>
                <input type = "text" placeholder='username' name='username' onChange={handleChange}/>
                <input type = "password" placeholder='password' name='password' onChange={handleChange}/>
                <button onClick={handleSubmit}>Login</button>
                <span>Don't have an account? <Link to="/register">Register</Link>
                </span>
            </form>
        </div>
    )
}

export default Login
