import React,{useContext} from 'react'
import { userTracker } from '../context/userTracker'
import { useState } from 'react'
import axios from "axios"
import {Link,useNavigate} from 'react-router-dom'

const EditProfile = () => {

    //user tracker
    const {currentUser,setCurrentUser} = useContext(userTracker)

    const [inputs,setInputs] = useState({
        username:"",
    })

    console.log(inputs)

    //function to handle change in input
    const handleChange = e => {
        setInputs(prev=>({...prev, [e.target.name]: e.target.value}))
    }



    const handleApply = async e => {
        try{
            //when call goes through
            const res = await axios.post("http://localhost:8800/edit/edituser",[inputs,currentUser])
            console.log(res)
        }catch(err)
        {
            //when theres an error
            console.log(err)
        }
    }

    return(
        <div className='edit'>
            <h1>
                Edit Profile
            </h1>
            <div className='inputs'>
                <h3>
                    user
                </h3>
                <input type = "text" placeholder='username' name='username' onChange={handleChange}/>
                <button onClick={handleApply}>
                    apply
                </button>
                <h3>
                    first name
                </h3>
                <input type = "text" placeholder='first name' name='username'/>
                <button>
                    apply
                </button>
                <h3>
                    last name
                </h3>
                <input type = "text" placeholder='last name' name='username'/>
                <button>
                    apply
                </button>
                <h3>
                    school
                </h3>
                <input type = "text" placeholder='school' name='username'/>
                <button>
                    apply
                </button>
            </div>


        </div>
    )
}

export default EditProfile
